<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');
class Fin_labarugi_perperiod extends CI_Controller {
	function __construct(){
		parent::__construct();			
	}	
		public function index()	{		if($this->input->post("tgl1")== "")		{			$tgl1 = 0;			$tgl2 = 0;			$data['pemasukan'] = $this->app_model->fin_pemasukan_p($tgl1,$tgl2);			$data['pemasukan_total'] = $this->app_model->fin_pemasukan_sum_p($tgl1,$tgl2)->row()->total;			$data['pengeluaran'] = $this->app_model->fin_pengeluaran_p($tgl1,$tgl2);			$data['pengeluaran_total'] = $this->app_model->fin_pengeluaran_sum_p($tgl1,$tgl2)->row()->total;		}		else		{			//simpan session			$sess_data['mulai'] = $this->input->post("tgl1");				$sess_data['akhir'] = $this->input->post("tgl2");						$this->session->set_userdata($sess_data);			//pangggil session			$tgl1 = $this->session->userdata('mulai');			$tgl2 = $this->session->userdata('akhir');									$data['pemasukan'] = $this->app_model->fin_pemasukan_p($tgl1,$tgl2);			$data['pemasukan_total'] = $this->app_model->fin_pemasukan_sum_p($tgl1,$tgl2)->row()->total;			$data['pengeluaran'] = $this->app_model->fin_pengeluaran_p($tgl1,$tgl2);			$data['pengeluaran_total'] = $this->app_model->fin_pengeluaran_sum_p($tgl1,$tgl2)->row()->total;		}				$data['komponen_top'] = array('navbar','forcelogin');		$data['template'] = 'satucolumn';		$data['home'] = '';				$data['interface'] = array('fin_labarugi_perperiod');				$this->load->view('index',$data);	}		public function cetak()	{		//pangggil session		$tgl1 = $this->session->userdata('mulai');		$tgl2 = $this->session->userdata('akhir');													$data['pemasukan'] = $this->app_model->fin_pemasukan_p($tgl1,$tgl2);		$data['pemasukan_total'] = $this->app_model->fin_pemasukan_sum_p($tgl1,$tgl2)->row()->total;		$data['pengeluaran'] = $this->app_model->fin_pengeluaran_p($tgl1,$tgl2);		$data['pengeluaran_total'] = $this->app_model->fin_pengeluaran_sum_p($tgl1,$tgl2)->row()->total;				//$data['list_field'] = $this->db->list_fields($table);		$data['komponen_top'] = array('nav-print','lap_keu_labarugi');		$this->load->view('index',$data);	}	
